-- SQL compatible con phpMyAdmin (MySQL/MariaDB)
SET SQL_MODE='ANSI_QUOTES';
SET FOREIGN_KEY_CHECKS=0;
CREATE TABLE IF NOT EXISTS "migrations" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "migration" varchar(255) not null, "batch" int not null);
CREATE TABLE IF NOT EXISTS "users" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "username" varchar(255) not null, "password" varchar(255) not null, "email" varchar(255) not null, "email_verified_at" datetime, "remember_token" varchar(255), "created_at" datetime, "updated_at" datetime, "profile_image_path" varchar(255) not null default 'profiles/default.png', "name" varchar(255), "topics_comment_count" int not null default '0', "disabled" tinyint(1) not null default '0', "ip_register" varchar(255) not null, "ip_last_login" varchar(255) not null, "last_login" datetime not null, "forum_signature" text, "habbo_name" varchar(255), "habbo_hotel" varchar(255), "habbo_verification_code" varchar(255), "habbo_verified_at" datetime);
CREATE TABLE IF NOT EXISTS "password_resets" ("email" varchar(255) not null, "token" varchar(255) not null, "created_at" datetime);
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "uuid" varchar(255) not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime default CURRENT_TIMESTAMP not null);
CREATE TABLE IF NOT EXISTS "topics_categories" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "name" varchar(255) not null, "icon" varchar(255));
CREATE TABLE IF NOT EXISTS "topics" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "title" varchar(255) not null, "category_id" int not null, "user_id" int not null, "content" text not null, "moderated" varchar(255) not null default 'pending', "moderator" varchar(255), "slug" varchar(255) not null, "fixed" tinyint(1) not null default '0', "status" tinyint(1) not null default '1', "comments_count" int not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("category_id") references "topics_categories"("id"), foreign key("user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "topics_comments" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "topic_id" int not null, "content" text not null, "created_at" datetime, "updated_at" datetime, "active" tinyint(1) not null default '1', "moderated" varchar(255) not null default 'pending', "moderator" varchar(255), foreign key("user_id") references "users"("id"), foreign key("topic_id") references "topics"("id"));
CREATE TABLE IF NOT EXISTS "articles_categories" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "name" varchar(255) not null, "icon" varchar(255));
CREATE TABLE IF NOT EXISTS "articles" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "category_id" int not null, "title" varchar(255) not null, "description" varchar(255) not null, "image_path" varchar(255) not null, "slug" varchar(255) not null, "content" text not null, "reviewed" tinyint(1) not null default '0', "reviewer" varchar(255), "status" tinyint(1) not null default '0', "fixed" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"), foreign key("category_id") references "articles_categories"("id"));
CREATE TABLE IF NOT EXISTS "slides" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "title" varchar(255) not null, "description" varchar(255) not null, "slug" varchar(255) not null, "image_path" varchar(255) not null, "active" tinyint(1) not null default '0', "fixed" tinyint(1) not null default '0', "new_tab" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "articles_comments" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "article_id" int not null, "content" text not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"), foreign key("article_id") references "articles"("id"));
CREATE TABLE IF NOT EXISTS "users_bans" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "admin_id" int not null, "type" varchar(255) not null default 'account', "reason" varchar(255), "expires_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"), foreign key("admin_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "users_warnings" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "admin_id" int not null, "reason" varchar(255), "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"), foreign key("admin_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "users_logs" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "user_id" int not null, "ip" varchar(255) not null, "content" varchar(255) not null, "browser" text not null, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "navigations" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "label" varchar(255) not null, "small_icon" varchar(255), "hover_icon" varchar(255), "slug" varchar(255), "order" int not null default '0', "visible" tinyint(1) not null default '1');
CREATE TABLE IF NOT EXISTS "sub_navigations" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "navigation_id" int not null, "label" varchar(255) not null, "slug" varchar(255), "new_tab" tinyint(1) not null default '0', "order" int not null default '0', "visible" tinyint(1) not null default '1', foreign key("navigation_id") references "navigations"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "badges" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "title" varchar(255) not null, "description" varchar(255), "code" varchar(255) not null, "image_path" varchar(255) not null, "rarity" varchar(255) not null default 'normal', "content_slug" varchar(255), "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "user_badges" ("user_id" int not null, "badge_id" int not null, foreign key("user_id") references "users"("id") on delete cascade, foreign key("badge_id") references "badges"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "user_notifications" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "to_user_id" int not null, "from_user_id" int not null, "type" varchar(255) not null default 'info', "title" varchar(255) not null, "slug" varchar(255), "user_saw" tinyint(1) not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("to_user_id") references "users"("id"), foreign key("from_user_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "furni_categories" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "name" varchar(255) not null, "icon" varchar(255));
CREATE TABLE IF NOT EXISTS "furni_values" ("id" int NOT NULL AUTO_INCREMENT PRIMARY KEY, "name" varchar(255) not null, "category_id" int not null, "admin_id" int not null, "price" int, "price_type" varchar(255) not null default 'coins', "state" varchar(255) not null default 'regular', "icon_path" varchar(255), "image_path" varchar(255) not null, "created_at" datetime, "updated_at" datetime, foreign key("category_id") references "furni_categories"("id"), foreign key("admin_id") references "users"("id"));
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "password_resets_email_index" on "password_resets" ("email");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "topics_categories_name_unique" on "topics_categories" ("name");
CREATE UNIQUE INDEX "badges_code_unique" on "badges" ("code");
CREATE INDEX "user_badges_user_id_index" on "user_badges" ("user_id");
CREATE INDEX "user_badges_badge_id_index" on "user_badges" ("badge_id");
CREATE UNIQUE INDEX "users_username_unique" on "users" ("username");
CREATE UNIQUE INDEX "users_habbo_name_hotel_unique" on "users" ("habbo_name", "habbo_hotel");
SET FOREIGN_KEY_CHECKS=1;
